  `id` int(11) NOT NULL AUTO_INCREMENT,
  `gid` int(11) DEFAULT NULL COMMENT '关联广告ID',
  `title` varchar(50) DEFAULT NULL COMMENT '标题',
  `url` varchar(255) DEFAULT NULL COMMENT '广告地址',
  `pattern` int(11) DEFAULT NULL COMMENT '推广模式,1:CPC,0：CPV',
  `price` int(11) DEFAULT NULL COMMENT '单价',
  `matter` varchar(50) DEFAULT NULL COMMENT '素材',
  `bprice` int(11) DEFAULT NULL COMMENT '预算价格',
  `status` int(11) DEFAULT NULL COMMENT '状态:0未审核1已审核2驳回',
  `create_time` int(11) DEFAULT NULL COMMENT '创建时间',
  `uid` int(11) DEFAULT NULL COMMENT '广告主人ID',
  `tgimg` text NOT NULL COMMENT '推广素材图片',
  `sqimg` text NOT NULL COMMENT '授权资质证明',
  `describe` text NOT NULL COMMENT '描述',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;

