  `avatar` varchar(255) DEFAULT NULL COMMENT '微信头像',
  `status` int(11) DEFAULT NULL COMMENT '状态:0待审核,1审核通过',
  `create_time` varchar(50) DEFAULT NULL COMMENT '创建时间',
  `last_time` varchar(50) DEFAULT NULL COMMENT '最后登陆时间',
  `last_ip` varchar(50) DEFAULT NULL COMMENT '最后登录IP',
  `money` int(11) DEFAULT NULL COMMENT '累计收入',
  `balances` int(11) DEFAULT NULL COMMENT '余额',
  `consumption` int(11) NOT NULL COMMENT '消费累计',
  `Companyname` varchar(100) NOT NULL COMMENT '企业名称',
  `openid` varchar(100) NOT NULL COMMENT '微信授权',
  PRIMARY KEY (`id`)
